//#7BA9D4 투명도 70%
* {
  margin: 0;
  padding: 0;
}
li {
  list-style: none;
}
a {
  text-decoration: none;
  color: #000;
}
img {
  width: 100%;
  height: auto;
  display: block;
}

//---------------------------------------

.section {
  text-align: center;
  background-color: rgba(123, 169, 212, 0.7);
  border-bottom: 1px solid #fff;
  box-sizing: border-box;
  border-left: none;
  border-right: none;
}

//01

.container01 {
  background-color: rgba(123, 169, 212, 0.7);
  text-align: center;
  border-top: 1px solid #fff;
  h1 {
    color: rgba(255, 255, 255, 0.5);
    font-size: 100px;
  }
  h2 {
    color: rgba(255, 255, 255, 0.5);
    font-size: 100px;
  }
}
.container02 {
  .profile_outer {
    width: 1300px;
    height: 600px;
    margin: 0 auto;
    h1 {
      color: rgba(255, 255, 255, 0.8);
      font-size: 50px;
      margin: 10px;
    }
    .profile_box {
      width: 1080px;
      height: 100%;
      display: flex;
      justify-content: center;
      align-items: center;
      background-color: rgba(255, 255, 255, 0.5);
      margin: 0 auto;
      border-radius: 12px;
      .img_box {
        width: 50%;
        height: 346px;
        overflow: hidden;
        img {
          border-radius: 12px;
          width: 310px;
          height: 100%;
          margin: 0 auto;
        }
      }
      .text_box {
        width: 50%;
        height: 100%;
        padding-top: 160px;
        box-sizing: border-box;
        text-align: left;
        padding-left: 50px;
        p {
          margin-bottom: 20px;
        }
        a {
          text-decoration: underline;
        }
      }
    }
  }
}
.container03 {
  h1 {
    color: rgba(255, 255, 255, 0.7);
    font-size: 50px;
    margin: 10px;
  }
  .skill_box_outer {
    width: 1080px;
    height: 800px;
    margin: 0 auto;
    background-color: rgba(255, 255, 255, 0.5);
    border-radius: 12px;
    .skill_box {
      width: 860px;
      height: 100%;
      border-radius: 12px;
      display: flex;
      flex-wrap: wrap;
      margin: 0 auto;
      align-items: center;
      .skills {
        width: 420px;
        height: 90px;
        display: flex;
        margin-right: 20px;
        .skill_img {
          width: 90px;
          height: 90px;
          margin-right: 20px;
        }
        .skill_info {
          width: 310px;
          height: 90px;
          background-color: rgba(255, 255, 255, 0.5);
          border-radius: 12px;
          padding: 10px;
          box-sizing: border-box;
        }
      }
      .skills:nth-child(2n) {
        margin-right: 0;
      }
      .skills08,
      .skills10 {
        .skill_img {
          /* padding-top: 45px; */
          box-sizing: border-box;
          img {
            margin-top: 22px;
          }
        }
      }
    }
  }
}
.container04 {
  width: 1300px;
  height: auto;
  h1 {
    font-size: 50px;
    color: rgba(255, 255, 255, 0.7);
    margin: 10px;
  }
  .swiper {
    width: 1080px;
    height: auto;
  }
}
